import React from 'react'
import './Header.css'
import { Form, Col, Row} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function Header() {

    // $(document).ready(function(){
    //     var x = screen.width;
    //     if (x <= $.parseMethod("376px")){
    //         $(".header").append(" <b>Appended text</b>.");
    //     }
    //   });

    return (
        <div className="header">
            <div class="container">
                <div className="row">
                <ul className="nav">
                    <div className="menu_left">
                            <div className="logo_menu_bars text-light"><a><i class="fa-2x fas fa-bars"></i></a></div>
                            <div className="logo_icon_youtube"><a><i class=" fa-2x text-danger fab fa-youtube"></i></a></div>
                            <div className="logo_text_youtube text-light"><h5 className="">YouTube<sup><a>VN</a></sup></h5></div>
                    </div>
                </ul>
                <ul className="nav">
                    <div className="menu_center">
                       <input className="input_and_icon" type="text" placeholder="Tìm kiếm"></input>
                       <button className="button_search text-light" type="submit"><i class="fa fa-search"></i></button>
                       <button className="button_microphone text-light" type="submit"><i class="fas fa-microphone"></i></button>
                    </div>
                </ul>

                <ul className="nav">
                    <div className="menu_right">
                    <button className="button_camera text-light" type="submit"><i class="fa fa-camera" aria-hidden="true"></i></button>
                    <button className="button_app text-light" type="submit"><i class="fa fa-th" aria-hidden="true"></i></button>
                    <button className="button_bell text-light" type="submit"><i class="fa fa-bell" aria-hidden="true"></i></button>
                    <button className="button_user text-light" type="submit"><i class="fa fa-user" aria-hidden="true"></i></button>
                    </div>
                </ul>
                <button className="button_user_02  text-light" type="button"><i class="fa fa-user-circle" aria-hidden="true"></i></button>
                </div>
                
            </div>
        </div>
    )
}
