import React from 'react'
import './Body.css'
// import './Body_test'
export default function Body() {

    return (
        <div className="body_youtube">    

            <div className="menubar_left">
                <div className="btn-group-vertical">
                    <button type="button" class="btn  text-dark px-4 py-3"><i class="fa fa-home" aria-hidden="true"></i><p>Home</p></button>
                    <button type="button" class="btn  text-dark px-4 py-3"><i class="fab fa-hotjar"></i><p>Trending</p></button>
                    <button type="button" class="btn  text-dark px-4 py-3"><i class="fa fa-window-restore" aria-hidden="true"></i><p>Subsription</p></button>
                    <button type="button" class="btn  text-dark px-4 py-3"><i class="fas fa-gamepad"></i><p>Play game</p></button>
                </div>
            </div>

            <div className="slide_menuBar">
                <div className="">
                    <div class="carousel slide" id="myCarousel" data-ride="carousel" data-interval="2000" data-pause="hover">
                            <div className="carousel-inner">
                                <div className="item active">
                                    <div className="col-xs-3"><a href="#">thu hoa 1</a></div>
                                </div>
                                <div className="item">
                                    <div className="col-xs-3"><a href="#">thu hoa 2</a></div>
                                </div>
                                <div className="item">
                                    <div className="col-xs-3"><a href="#"> Thu hoa 3</a></div>
                                </div>
                                <div className="item">
                                    <div className="col-xs-3"><a href="#">Thu hoa 4</a></div>
                                </div>
                                <div className="item">
                                    <div className="col-xs-3"><a href="#">Thu hoa 5</a></div>
                                </div>
                                <div className="item">
                                 <div className="col-xs-3"><a href="#">Thu hoa 6</a></div>
                                </div>
                                <div className="item">
                                    <div className="col-xs-3"><a href="#">Thu hoa 7</a></div>
                                </div>
                                <div className="item">
                                    <div className="col-xs-3"><a href="#">Thu hoa 8</a></div>
                                </div>
                            </div>
                            <a className="left carousel-control" href="#myCarousel" data-slide="prev"><i className="glyphicon glyphicon-chevron-left"></i></a>
                            <a className="right carousel-control" href="#myCarousel" data-slide="next"><i className="glyphicon glyphicon-chevron-right"></i></a>
                    </div>
                            
                </div>
            </div> 

        </div>
    )
}

$('#myCarousel').carousel({
    interval: 4000
  })
  
  $('.carousel .item').each(function(){
    var next = $(this).next();
    if (!next.length) {
      next = $(this).siblings(':first');
    }
    next.children(':first-child').clone().appendTo($(this));
    
    for (var i=0;i<2;i++) {
      next=next.next();
      if (!next.length) {
          next = $(this).siblings(':first');
        }
      
      next.children(':first-child').clone().appendTo($(this));
    }
  });
